    while

    public class Solution {
 /**
  * @param a: An integer
  * @param b: An integer
  * @return: The sum of a and b 
  */
 public int aplusb(int a, int b) {
     // write your code here
   int sum=a^b;
   int carry=(a&amp;b)&lt;&lt;1;
   while(carry!=0){
       a=sum;
       b=carry;
       sum=a^b;
       carry=(a&amp;b)&lt;&lt;1;
   }
   return sum;
 }
}
